mapper.select:
select
  order_detail_id, order_no, product_id, number, product_name, product_price, product_sku, status
from JD_SHOP_ORDER.order_detail


        and order_detail_id = #{orderDetailId}


        and order_no = #{orderNo}


        and product_id = #{productId}


        and number = #{number}


        and product_name = #{productName}


        and product_price = #{productPrice}


        and product_sku = #{productSku}


        and status = #{status}
